mapper.update:
update t_app_type
type_name=#{typeName}, description=#{description}, parent=#{parent}, visible=#{visible}, order_no=#{orderNo}
where id = #{id}

mapper.select:
select id, type_name, description, parent, visible, order_no from t_app_type where 1=1
and type_name like '%${typeName}%'
and parent = #{parent}
and visible = #{visible}
order by order_no asc
