mapper.update:
update t_goods
goods_code=#{goodsCode}, goods_name=#{goodsName}, display_name=#{displayName}, shop_id=#{shopId}, brand_id=#{brandId}, creater_uid=#{createrUid}, cate_id=#{cateId}, cost_price=#{costPrice}, sell_price=#{sellPrice}, comm_rate=#{commRate}, comm_price=#{commPrice}, goods_pic=#{goodsPic}, brief=#{brief}, content=#{content}, like_num=#{likeNum}, isTop=#{isTop}, isHot=#{isHot}, indexHot=#{indexHot}, status=#{status}, preview=#{preview}, sort=#{sort}, keyword=#{keyword}, stocks=#{stocks}, l_icon=#{lIcon}, s_icon=#{sIcon}, link_url=#{linkUrl}, buys=#{buys}, del_flag=#{delFlag}, modifytime=now()
where id = #{id}

mapper.select:
select from t_goods where 1=1 and del_flag = 0
and goods_name like '%${goodsName}%'
order by sort asc limit #{offset},#{pageSize}
